import React from 'react';


import aboutimg from './images/aboutus.png';

const About = () => {

    const clientImages = [
        './images/brand1.jpg',
        './images/brand1.jpg',
        './images/brand1.jpg',
        './images/brand1.jpg'
        // Add more client images as needed
    ];

    const styles = {
        container: {
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '100vh',
            backgroundImage: `url(${aboutimg})`,
            backgroundSize: 'cover',

        },
        heading: {
            textAlign: 'center',
            color: '#fff',
            fontSize: '32px',
            fontWeight: 'bold',
            textShadow: '2px 2px 4px rgba(0, 0, 0, 0.5)',
            marginTop: '-70vh',

        },

        section: {
            padding: '40px 0',
            background: '#f5f5f5',
          },
          heading: {
            textAlign: 'center',
            marginBottom: '20px',
          },
          clientImages: {
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            flexWrap: 'wrap',
          },
          clientImage: {
            width: '150px',
            height: '150px',
            objectFit: 'cover',
            margin: '10px',
          },

    };
    return (
        <>
            <div style={styles.container}>
                <h1 style={styles.heading}>About Us</h1>
            </div>


            <section className="our-clients">
                <h2>Our Clients</h2>
                <div className="client-images">
                    {clientImages.map((image, index) => (
                        <img
                            key={index}
                            src={image}
                            alt={`Client ${index + 1}`}
                            className="client-image"
                        />
                    ))}
                </div>
            </section>
        </>
    )
}

export default About
