import React from 'react';
import client1 from './images/brand1.jpg';
import client2 from './images/brand2.png';
import client3 from './images/brand3.png';


import aboutimg from './images/aboutus.png';

const About = () => {


    const styles = {
        container: {
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '100vh',
            backgroundImage: `url(${aboutimg})`,
            backgroundSize: 'cover',

        },
        heading: {
            textAlign: 'center',
            color: '#fff',
            fontSize: '32px',
            fontWeight: 'bold',
            textShadow: '2px 2px 4px rgba(0, 0, 0, 0.5)',
            marginTop: '-70vh',

        },



    };
    return (
        <>
            <div style={styles.container}>
                <h1 style={styles.heading}>About Us</h1>
            </div>

            {/* clients */}
            <section className="our-clients">
                <div className="container">
                    <h2 className="text-center fw-bold text-white">Our Clients</h2>
                    <div className="row">
                        <div className="col-md-3 col-sm-6">
                            <img
                                src={client1}
                                alt="Image description"

                                style={{ marginRight: '80px', }}
                            />
                        </div>
                        <div className="col-md-3 col-sm-6">
                            <img
                                src={client1}
                                alt="Image description"

                                style={{ marginRight: '80px', }}
                            />          </div>
                        <div className="col-md-3 col-sm-6">
                            <img
                                src={client1}
                                alt="Image description"

                                style={{ marginRight: '80px', }}
                            />          </div>
                    </div>
                    <div className="row">
                        <div className="col-md-3 col-sm-6">
                            <img
                                src={client1}
                                alt="Image description"

                                style={{ marginRight: '80px', }}
                            />          </div>
                        <div className="col-md-3 col-sm-6">
                            <img
                                src={client1}
                                alt="Image description"

                                style={{ marginRight: '80px', }}
                            />          </div>
                        <div className="col-md-3 col-sm-6">
                            <img
                                src={client1}
                                alt="Image description"

                                style={{ marginRight: '80px', }}
                            />          </div>
                        <div className="col-md-3 col-sm-6">
                            <img
                                src={client1}
                                alt="Image description"

                                style={{ marginRight: '80px', }}
                            />          </div>
                    </div>
                    <div className="row">
                        {/* Add the remaining images for the third row */}
                    </div>
                    <div className="row">
                        {/* Add the remaining images for the fourth row */}
                    </div>
                </div>
            </section>


        </>
    )
}

export default About
