import React from 'react';
import client1 from './images/brand1.jpg';
import client2 from './images/brand2.png';
import client3 from './images/brand3.png';


import aboutimg from './images/aboutus.png';

const About = () => {


    const styles = {
        container: {
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '100vh',
            backgroundImage: `url(${aboutimg})`,
            backgroundSize: 'cover',

        },
        heading: {
            textAlign: 'center',
            color: '#fff',
            fontSize: '32px',
            fontWeight: 'bold',
            textShadow: '2px 2px 4px rgba(0, 0, 0, 0.5)',
            marginTop: '-70vh',

        },



    };
    return (
        <>
            <div style={styles.container}>
                <h1 style={styles.heading}>About Us</h1>
            </div>

            <section className="client-section">
                <Container>
                    <h2>Our Clients</h2>
                    <Row>
                        <Col sm={4}>
                            <img src={client1} alt="Client 1" className="client-image" />
                        </Col>
                        <Col sm={4}>
                            <img src={client2} alt="Client 2" className="client-image" />
                        </Col>
                        <Col sm={4}>
                            <img src={client3} alt="Client 3" className="client-image" />
                        </Col>
                    </Row>
                </Container>
            </section>

        </>
    )
}

export default About
