import event from './images/events.jpeg'
import React, { useState } from 'react';
import { Modal, Button } from 'react-bootstrap';


function Event1() {

    const headerStyle = {
        backgroundImage: `url(${event})`,
        backgroundSize: '1297px',
        backgroundAttachment: 'fixed',
        height: '30vh',
        position: 'relative',
    };

    const headingStyle = {
        color: '#ffffff',
        fontSize: '2rem',
        textTransform: 'uppercase',
        textAlign: 'center',
    };

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <>
            <header style={headerStyle}>
                <h1 style={headingStyle}>Our Work!</h1>
            </header>
            <div class="container">
                <h1 class="big-heading">Events:Unforgettable moments, boundless possibilities</h1>
            </div>


            <div className="d-flex justify-content-center align-items-center" style={{ height: '100vh' }}>
                <Button variant="primary" onClick={handleShow}>
                    Launch Modal
                </Button>

                <Modal show={show} onHide={handleClose} centered>
                    <Modal.Header closeButton>
                        <Modal.Title>Vertically Centered Modal</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <p>This is the content of the modal.</p>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" onClick={handleClose}>
                            Save Changes
                        </Button>
                    </Modal.Footer>
                </Modal>
            </div>
        </>
    );
}

export default Event1
